import classes from './SidePar.module.css'
import {Categories} from './Icons'
import { useSelector } from 'react-redux'
function SidePar (props) {
    const state = useSelector(state => state.Subscibe)
    return (
        <div className={classes.sidePar}>
            <ul>
                {Categories.MainCategory.map((category) => {
                    return <li style={props.selectedCategory === category.name ? {backgroundColor: 'red'} : null} key={category.name} onClick={() => {props.setSelectedCategory(category.name)}}>
                        {category.icon} {category.name}
                        </li>
                })}
                <span></span><br></br>
                <p className={classes.title}>Subscribtions</p>
                <ul>
                    {state.map((channel) => {
                    return <li style={props.selectedCategory === channel.name ? 
                        {backgroundColor: 'red'} : null}
                    key={channel.name}
                    onClick={() => {props.setSelectedCategory(channel.name)}}>{channel.name}</li>
                })}
                </ul>   
                <h6>Created by Mohamed ELsheta</h6>
            </ul>
        </div>
    )
}
export default SidePar
