import React, { useEffect , useState } from 'react'
import { useParams } from 'react-router-dom'    
import FetchFromApi from './Fetching/FetchFromApi' 
import classes from './ChannelDetails.module.css' 
function ChannelDetails() {
    const id = useParams()
    const [ChannelDetails , setChannelDetails] = useState(null)
    const [videos , setVedeos] = useState([])
    useEffect(()=> {
        console.log()
        FetchFromApi(`channels?part=snippet%2Cstatistics&id=${id.id}`).then((data)=>{
            setChannelDetails(data.data.items[0])
        })

        FetchFromApi(`search?channelId=${id.id}&part=snippet%2Cid&order=date`).then((data)=>{
            setVedeos(data.data.items)
        })
    }, [id])
    if(ChannelDetails){
        console.log(ChannelDetails.snippet.localized.title)
    }
    
    console.log(videos)
    return (
        <div className={classes.channel}>
            <div className={classes.gradient} />
            { ChannelDetails && <div className={classes.channelDetails}>
                <img src={ChannelDetails.snippet.thumbnails.high.url} alt='there is an img' />  
                <h3>{ChannelDetails.snippet.localized.title}</h3>
                <p></p>
            </div> }
        </div>
    )
}

export default ChannelDetails
