import React from 'react'
import { useEffect , useState } from 'react'
import ReactPlayer from 'react-player'
import { Link, useParams } from 'react-router-dom'
import FetchFromApi from './Fetching/FetchFromApi'
import classes from './VidioDetails.module.css'
import VerifiedIcon from '@mui/icons-material/Verified';
import VisibilityIcon from '@mui/icons-material/Visibility';
import ThumbUpIcon from '@mui/icons-material/ThumbUp';
function VidioDetails() {
    const {id} = useParams()
    const [vidioDetails , setVidioDetails] = useState('')
    useEffect(()=>{
        FetchFromApi(`videos?part=contentDetails%2Csnippet%2Cstatistics&id=${id}`).then((data)=>{
            setVidioDetails(data.data.items[0])
        })
    }, [id])
    if (!vidioDetails) {
        return 'Loading'
    }
    console.log()
    return (
        <div className={classes.VidioContent}>
            {vidioDetails && <div className={classes.vidio}  >
            
                <ReactPlayer 
                url={`https://www.youtube.com/watch?v=${id}`}
                width="100%"
                height="100%"
                controls
               />
                <div className={classes['vidio-details']}>
                    <div>
                        <h2>{vidioDetails.snippet.title}</h2>
                        <Link to={`/channel/${vidioDetails.snippet.channelId}`}>{vidioDetails.snippet.channelTitle}</Link><VerifiedIcon />
                    </div>
                    <p className={classes.statics}>
                        <span>{vidioDetails.statistics.viewCount}&nbsp; <h6 >Views</h6> </span>
                        <br/>
                        <span>{vidioDetails.statistics.likeCount} &nbsp;<h6 >Likes</h6> </span>
                    </p>
                </div>
            </div> }
            
            <div className={classes.playlist}>
                
            </div>
        </div>
    )
}

export default VidioDetails
