import React from 'react'
import { useEffect , useState } from 'react'
// import ReactPlayer from 'react-player'
import { Link, useParams } from 'react-router-dom'
import FetchFromApi from './Fetching/FetchFromApi'
import classes from './VidioDetails.module.css'

function VidioDetails() {
    const {id} = useParams()
    const [vidioDetails , setVidioDetails] = useState('')
    useEffect(()=>{
        FetchFromApi(`videos?part=contentDetails%2Csnippet%2Cstatistics&id=${id}`).then((data)=>{
            setVidioDetails(data.data.items[0])
        })
    }, [id])
    if (!vidioDetails) {
        return 'Loading'
    }
    console.log()
    return (
        <div className={classes.VidioContent}>
            {vidioDetails && 
            <div className={classes.vidio}>
                
                <video width="750" height="500" controls >
                    <source src={`https://www.youtube.com/watch?v=${id}`} type="video/mp4"/>
                </video>
                <div className={classes['vidio-details']}>
                    <h2>{vidioDetails.snippet.title}
                        <Link to={`/channel/${vidioDetails.snippet.channelId}`}>{vidioDetails.snippet.channelTitle}</Link>
                    </h2>
                    <p className={classes.statics}>
                        <span>{vidioDetails.statistics.viewCount}</span>
                        <span>{vidioDetails.statistics.likeCount}</span>
                    </p>
                </div>
            </div> }
            
            <div className={classes.playlist}>

            </div>
        </div>
    )
}

export default VidioDetails
