import classes from './SidePar.module.css'
import {Categories} from './Icons'
import { useEffect, useState } from 'react'
function SidePar (props) {

    const [selectedCategory , setSelectedCategory] = useState('Home')
    useEffect(()=>{
        props.ongetSelected(selectedCategory)
    }, [selectedCategory])
    return (
        <div className={classes.sidePar}>
            <ul>
                {Categories.MainCategory.map((category) => {
                    return <li style={selectedCategory === category.name ? {backgroundColor: 'red'} : null} key={category.name} onClick={() => {setSelectedCategory(category.name)}}>
                        {category.icon} {category.name}
                        </li>
                })}
                <span></span>
                <ul>
                    {Categories.altCategory.map((category) => {
                    return <li key={category.name}>{category.icon} {category.name}</li>
                })}
                </ul>   
                <h6>Created by Mohamed ELsheta</h6>
            </ul>
        </div>
    )
}
export default SidePar
